    public function getSourceContext()
    {
        return new Source("<!DOCTYPE html>
<html>
    <head>
        <meta charset=\"UTF-8\">
        <title>{% block title %}Welcome!{% endblock %}</title>
        <link rel=\"stylesheet\" href=\"https://stackpath.bootstrapcdn.com/bootstrap/4.3.1/css/bootstrap.min.css\">
        {% block stylesheets %}{% endblock %}
    </head>
    <body>
        <nav class=\"navbar navbar-expand-lg navbar-dark bg-dark\">
            <a class=\"navbar-brand\" href=\"{{ path('home') }}\">Slide</a>
            <button class=\"navbar-toggler\" type=\"button\" data-toggle=\"collapse\" data-target=\"#navbarSupportedContent\" aria-controls=\"navbarSupportedContent\" aria-expanded=\"false\" aria-label=\"Toggle navigation\">
                <span class=\"navbar-toggler-icon\"></span>
            </button>

            <div class=\"collapse navbar-collapse\" id=\"navbarSupportedContent\">
                <ul class=\"navbar-nav mr-auto\">
                    <li class=\"nav-item\">
                        <a class=\"nav-link\" href=\"{{ path('home') }}\">Accueil</a>
                    </li>
                    {% if(app.user) %}
                        {% if is_granted('ROLE_ADMIN') %}
                            <li class=\"nav-item\">
                                <a class=\"nav-link\" href=\"{{ path('upload') }}\">Upload</a>
                            </li>
                        {% endif %}
                    {% endif %}
                </ul>
                <ul class=\"navbar-nav ml-auto\">
                    {% if(app.user) %}
                        <li class=\"nav-item\">
                            <a class=\"nav-link\" href=\"/logout\">Déconnexion</a>
                        </li>
                    {% else %}
                        <li class=\"nav-item\">
                            <a class=\"nav-link\" href=\"{{ path('login') }}\">Connexion</a>
                        </li>
                        <li class=\"nav-item\">
                            <a class=\"nav-link\" href=\"{{ path('register') }}\">Inscription</a>
                        </li>
                    {% endif %}
                </ul>
            </div>
        </nav>
        <div class=\"container mt-3\">
            {% for message in app.flashes('success') %}
                <div class=\"alert alert-success\" role=\"alert\">
                    <h4 class=\"alert-heading\">Succès !</h4>
                    <p>{{ message }}</p>
                </div>
            {% endfor %}

            {% block body %}{% endblock %}
        </div>

        <script src=\"https://code.jquery.com/jquery-3.3.1.slim.min.js\"></script>
        <script src=\"https://cdnjs.cloudflare.com/ajax/libs/popper.js/1.14.7/umd/popper.min.js\"></script>
        <script src=\"https://stackpath.bootstrapcdn.com/bootstrap/4.3.1/js/bootstrap.min.js\"></script>
        {% block javascripts %}{% endblock %}
    </body>
</html>
", "base.html.twig", "/home/pierre/Documents/SlideShow/templates/base.html.twig");
